mapper.insert:
INSERT INTO movie_genre (movie_num, genre_num) VALUES
(#{movie_genre.movieNum}, #{movie_genre.genreNum})
ON DUPLICATE KEY UPDATE movie_num = VALUES(movie_num);

INSERT INTO actor (actor_num, actor_name, actor_gender) VALUES
(#{actor.actorId}, #{actor.actorName}, #{actor.gender})
ON DUPLICATE KEY UPDATE actor_name = VALUES(actor_name), actor_gender = VALUES(actor_gender);

INSERT INTO movie (movie_num, movie_title, movie_release_date, movie_poster_path, movie_overview, movie_adult) VALUES
(#{movie.movieNum}, #{movie.movieTitle}, #{movie.movieReleaseDate}, #{movie.moviePosterPath}, #{movie.movieOverview}, #{movie.movieAdult})
ON DUPLICATE KEY UPDATE movie_title = VALUES(movie_title), movie_release_date = VALUES(movie_release_date), movie_poster_path = VALUES(movie_poster_path), movie_overview = VALUES(movie_overview), movie_adult = VALUES(movie_adult);

INSERT INTO actor_movie (movie_num, actor_num, actor_order, character_name) VALUES
(#{am.movieNum}, #{am.actorNum}, #{am.actorOrder}, #{am.characterName})
ON DUPLICATE KEY UPDATE actor_order = VALUES(actor_order), character_name = VALUES(character_name);
